import React, {Component} from 'react';
import './App.css';
import Lobby from './components/Lobby.js';

class App extends Component{
    state = {
        id: 5,
        allPlayers: [
            {
                id: 1,
                name: "DJ COW",
                ready: true,
                imgURL: 'https://vignette.wikia.nocookie.net/pewdiepieminecraft/images/0/06/MushCow2.jpeg/revision/latest?cb=20190803151753'

            },
            {
                id: 2,
                name: "Bengt",
                ready: true,
                imgURL: 'https://vignette.wikia.nocookie.net/pewdiepieminecraft/images/e/e7/4C2C755B-FB01-4351-886E-077F5B03CF16.jpeg/revision/latest?cb=20190811175416'
            },
            {
                id: 3,
                name: "Sven",
                ready: true,
                imgURL:'https://vignette.wikia.nocookie.net/pewdiepieminecraft/images/d/dd/Sven.png/revision/latest?cb=20191130204116'
            },
            {
                id: 4,
                name: "Sven's BF",
                ready: true,
                imgURL:'https://vignette.wikia.nocookie.net/pewdiepieminecraft/images/b/ba/SvenYellow.jpeg/revision/latest?cb=20190814205003'
            },
            {
                id: 5,
                name: "Water Sheep",
                ready: false,
                imgURL:'https://vignette.wikia.nocookie.net/pewdiepieminecraft/images/d/db/Water_Sheep%27s_Return.png/revision/latest?cb=20191129235246'
            },
            {
                id: 6,
                name: "Ulla Britta",
                ready: true,
                imgURL:'https://vignette.wikia.nocookie.net/pewdiepieminecraft/images/6/6d/BCBBA22C-0452-4B35-8C67-52F8345D734F.jpeg/revision/latest?cb=20190805181119'
            },
            {
                id: 7,
                name: "Flip Flop",
                ready: true,
                imgURL:'https://vignette.wikia.nocookie.net/pewdiepieminecraft/images/c/c7/Flip_Flop.jpeg/revision/latest?cb=20190922230718'
            }
        ],
    };

    updatePlayerReadyState = (id, readyState) =>{
        this.setState({
                allPlayers: (
                    this.state.allPlayers.map((player) => {
                        if(player.id === id){
                            player.ready = readyState;
                        }
                        return player;
                    })
                )
        });
    };

    getPlayerById = (id) => {
        const allPlayers = this.state.allPlayers;
        let player;
        for(let i = 0; i < allPlayers.length; i++){
            player = allPlayers[i];
            if(player.id === id){
                return player;
            }
        }
        return {
            id: id,
            name: '',
            ready: false,
            imgURL: ''
        }
    };

    render() {
        return (
                <div className="App">
                    <Lobby
                        you={this.getPlayerById(this.state.id)}
                        allPlayers={this.state.allPlayers}
                        updatePlayerReadyState = {this.updatePlayerReadyState}
                    />
                </div>

        );
    }
}

export default App;
