import React, { Component } from 'react';
import './App.css';
import {
  createFakeLobby,
  setPlayerReadyStatus,
  ListenToPlayersInLobby,
  getPlayersInLobby,
  createNewGame,
  addPlayerToLobby,
  stopListeningToLobby
} from "./components/firebase-database.component";
import {
  signIn,
  initFirebaseAuth,
  signOut
} from "./components/firebase-authentication.component";
import Lobby from './components/Lobby.js';
import { v4 as uuidv4 } from 'uuid';


class App extends Component {

  state = {
    playersInLobby: [],
    lobbyPropsUpdate: false,
    appRender: false,
    booleanThatRamtinWants: false,
  };

  componentDidMount() {
    createFakeLobby();
    ListenToPlayersInLobby((data) => {
        this.setState({
            playersInLobby: data,
            lobbyPropsUpdate: (!(this.state.lobbyPropsUpdate))
        });
    });

    //if (!localStorage.getItem('you')) localStorage.setItem('you', uuidv4());
    //this.setState({youID: localStorage.getItem('you')});
    //console.log('in mount: ', this.state);

    initFirebaseAuth();
    signIn().then(res => {
      this.setState({appRender: !this.state.appRender, booleanThatRamtinWants: true});
      console.log('in signIn', this.getUserProps(res.user));
    });
  }

  getUserProps(user) {
    console.log('Current user: ', {user});
    return { 
      email: user.email, 
      uid: user.uid,
      name: user.displayName,
      pic: user.photoURL
    };
  }

  updatePlayerReadyState = (id, readyState) => {
    this.setState({
      allPlayers: (
        this.state.allPlayers.map((player) => {
          if (player.id === id) {
            player.ready = readyState;
          }
          return player;
        })
      )
    });
  };

  getPlayerById = (id) => {
    const allPlayers = this.state.playersInLobby;
    let player;
    for (let i = 0; i < allPlayers.length; i++) {
      player = allPlayers[i];
      if (player.playerId === id) {
        return player;
      }
    }
    return {
      id: id,
      name: '',
      ready: false,
      imgURL: ''
    }
  };


  render() {
    console.log('in render: ', this.state); 

    let you = getUserProps();
    console.log({you});

    return (
        <div className="App">
          <Lobby
            you={this.getPlayerById('Bengt')}
            allPlayers={this.state.playersInLobby}
            lobbyPropsUpdate={this.state.lobbyPropsUpdate}
            updatePlayerReadyState={setPlayerReadyStatus}
          />
        </div>
    );
  }
}

export default App;
