import React from 'react';
import {Map, Marker, InfoWindow, GoogleApiWrapper} from 'google-maps-react';
import PlayerMarker from './PlayerMarker.js';
import './MapView.css';

export class MapView extends React.Component {
  state = {
    targetLocation: {
      lat: 0,
      lng: 0
    }
  }
  target = null;

  render() {
    if (!this.props.loaded)
      return (
        <div>Loading...</div>
      );

    return (
      <Map
        google={this.props.google}
        zoom={14}
        initialCenter={{
          lat: 59.329323,
          lng: 18.068581
        }}
        onClick={(props, map, e) => {
          this.setState({
            targetLocation: {
              lat: e.latLng.lat(),
              lng: e.latLng.lng()
            }
          });
          console.log(this.state.targetLocation);
        }}
      >
        <Marker {...this.props}
          ref={element => {
            if (element)
              this.target = element.marker;
          }}
          name={'Target'}
          position={this.state.targetLocation}
        />
        <PlayerMarker {...this.props}
          targetLocation={this.state.targetLocation}
        />
      </Map>
    );
  }
}

export default GoogleApiWrapper({
  apiKey: ('AIzaSyD-HO6pALPiY9nrniME6UmHy3P3YUCv2Eg')
})(MapView)
