import React, { Component } from 'react';
import './App.css';
import {
    createFakeLobby,
    setPlayerReadyStatus,
    ListenToPlayersInLobby,
    addPlayerToLobby,
    removePlayerFromLobby,
    addPlayerToNewGame,
    getPlayersInLobby,
    createNewGame,
    stopListeningToLobby,
    ListenToPlayersInGame,
} from "./components/firebase-database.component";
import {
    signIn,
    initFirebaseAuth,
    signOut
} from "./components/firebase-authentication.component";
import Lobby from './components/Lobby.js';
import Home from './components/Home.js';
import MapView from './components/MapView.js';
import { Toast } from 'react-onsenui';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';
import PubNubReact from 'pubnub-react';


class App extends Component {


    constructor(props) {
        super(props);
        this.pubnub = new PubNubReact({
            publishKey: 'pub-c-c72ffc95-6f42-4bdf-8a95-04015fd20d32',
            subscribeKey: 'sub-c-ea26bb44-92b6-11ea-8dc6-429c98eb9bb1'
        });
        this.pubnub.init(this);
    }

    componentWillMount() {
        this.pubnub.subscribe({
            channels: ['positionChannel'],
            withPresence: true
        });

        // Listening to 'positionChannel'
        this.pubnub.getMessage('positionChannel', (msg) => {
            console.log('msg :>> ', 
                msg,
                '\nstate :>> ',
                this.state);
            if (this.state.huntingPlayer.playerId === msg.message.user.playerId) {
                this.setState({
                    targetLocation: msg.message.userLocation,
                })
            }
            console.log('from pubnub update this.state :>> ', this.state);
        });

        // this.pubnub.getStatus((st) => {
        // });
    }


    publishMyLocation = (loc) => {
        //console.log('this.props :>> ', this.state);
        //console.log('loc :>> ', loc);
        this.pubnub.publish({
            message: {
                user: this.state.user,
                userLocation: {
                    lat: loc.coords.latitude,
                    lng: loc.coords.longitude
                }
            },
            channel: 'positionChannel'
        });
    }

    componentWillUnmount() {
        this.pubnub.unsubscribe({
            channels: ['positionChannel']
        });
    }


    state = {
        playersInLobby: [],
        lobbyPropsUpdate: false,
        toast: {
            open: false,
            timeout: 2500,
            message: ''
        },
        myLocation: {
            lat: 0,
            lng: 0
        },
        targetLocation: {
            lat: 0,
            lng: 0
        },
        user: {},
        redirect: false,
        path: "/",
    };

    authStateObserver = (user) => {
        if (user) { // User is signed in!,
            this.setState({
                user: this.getUserProps(user),
                redirect: true,
                path: '/lobby'
            });
            // console.log({ user }, ' signed in');
        } else { // User is signed out!
            this.setState({
                user: {},
            });
            // console.log({ user }, ' signed out');
        }
    };

    componentDidMount() {
        ListenToPlayersInLobby((data) => {
            this.setState({
                playersInLobby: data,
                lobbyPropsUpdate: (!(this.state.lobbyPropsUpdate))
            });
        });

        // // the way of ramtin
        // ListenToPlayersInGame((data) => {

        //     let userIndex = this.getUserIndex(data); //returns index in players list of current user
        //     if (data.length >= 2) {
        //         let chasedIndex = userIndex - 1;
        //         let targetIndex = userIndex + 1;
        //         if (userIndex === data.length - 1) {
        //             targetIndex = 0;
        //         }
        //         if (userIndex === 0) {
        //             chasedIndex = data.length - 1;
        //         }

        //         console.log('Player name: ',
        //             this.state.user.displayName,
        //             '\nPlayer index: ', 
        //             {userIndex},
        //             "\n He's chasing: ",
        //             {targetIndex},
        //             "\n He's chased by: ",
        //             {chasedIndex});

        //         this.setState({
        //             playersInGame: data,
        //             chasedByPlayer: data[chasedIndex],
        //             huntingPlayer: data[targetIndex],
        //             gamePropsUpdate: (!(this.state.gamePropsUpdate))
        //         });
        //     }
        // });

        // the way of Ale, He's so random
        ListenToPlayersInGame((data) => {
            if (data.length >= 2) {
                let targetPlayer = data[Math.floor(Math.random() * data.length)];
                while (targetPlayer.playerId === this.state.user.playerId) {
                    targetPlayer = data[Math.floor(Math.random() * data.length)];
                }

                // console.log('Player: ',
                //         this.state.user,
                //         '\nTarget player: ', 
                //         {targetPlayer});

                this.setState({
                    playersInGame: data,
                    // chasedByPlayer: 
                    huntingPlayer: targetPlayer,
                    gamePropsUpdate: (!(this.state.gamePropsUpdate))
                });
            }
        });

        initFirebaseAuth(this.authStateObserver);
    }

    getUserIndex = (list) => {
        for (let i = 0; i < list.length; i++) {
            const player = list[i];
            if (player.id === this.state.user.id) {
                return i;
            }
        }
    }

    getUserProps = (user) => {
        // console.log('Current user: ', { user });
        return {
            email: user.email,
            playerId: user.uid,
            playerName: this.formatDisplayName(user.displayName),
            imgURL: user.photoURL,
        };
    };

    formatDisplayName = (name) => {
        let maxLength = 8;
        let firstName = this.getFirstName(name);
        if (firstName.length > maxLength) {
            return `${firstName.slice(0, maxLength)}...`;
        } else {
            return firstName;
        }
    };

    getFirstName = (name) => {
        return (name.split(" "))[0];
    };

    playerInLobby = (id) => {
        const allPlayers = this.state.playersInLobby;
        let player;
        for (let i = 0; i < allPlayers.length; i++) {
            player = allPlayers[i];
            if (player.playerId === id) {
                return true;
            }
        }
        return false;
    };

    updateToastMessage = (message) => {
        let toast = this.state.toast;
        toast["message"] = message;
        this.setState({
            toast: toast
        })
    };

    openToast = () => {
        let toast = this.state.toast;
        toast["open"] = true;
        this.setState({
            toast: toast
        });
        setTimeout(() => {
            toast["open"] = false;
            this.setState({
                toast: toast
            });
        }, this.state.toast.timeout)
    };

    renderRedirect = () => {
        if (this.state.redirect) {
            return <Redirect push to={{
                pathname: this.state.path,
                state: {

                }
            }} />
        } else {
            return <React.Fragment />
        }
    };

    // Maybe not needed with the authStateObserver in componentDidMount
    signOut = () => {
        this.setState({
            path: '/'
        });
        removePlayerFromLobby(this.state.user);
        signOut();
    };

    // signIn = () => {
    //     signIn().then(res => {
    //         this.setState({
    //             user: this.getUserProps(res.user),
    //             page: 1,
    //         })
    //     });
    // };

    render() {
        return (
            <div className="App">
                <Router>
                    {this.renderRedirect()}
                    <Route exact render={({ location }) => (
                        <Switch location={location}>
                            <Route exact path={["/lobby"]} render={props => {
                                const user = this.state.user;
                                const userInLobby = this.playerInLobby(user.playerId)
                                return (<Lobby
                                    user={user}
                                    userInLobby={userInLobby}
                                    allPlayers={this.state.playersInLobby}
                                    lobbyPropsUpdate={this.state.lobbyPropsUpdate}
                                    updatePlayerReadyState={setPlayerReadyStatus}
                                    addPlayerToLobbyFunc={addPlayerToLobby}
                                    removePlayerFromLobbyFunc={removePlayerFromLobby}
                                    updateToastMessage={this.updateToastMessage}
                                    openToast={this.openToast}
                                    debounceToast={this.state.toast.open}
                                    signOut={this.signOut}
                                    addPlayerToNewGame={addPlayerToNewGame}
                                />);
                            }} />
                            <Route exact path={["/map"]}>
                                <MapView
                                    user={this.state.user}
                                    playersInGame={this.state.playersInGame}
                                    huntingPlayer={this.state.huntingPlayer}
                                    chasedByPlayer={this.state.chasedByPlayer}
                                    publishMyLocation={this.publishMyLocation}
                                    targetLocation={this.state.targetLocation}
                                />
                            </Route>
                            <Route exact path={["/", "/index.html", "/*"]}>
                                <Home
                                    signIn={signIn}
                                    content={
                                        <React.Fragment>
                                        </React.Fragment>
                                    }
                                />
                            </Route>
                        </Switch>
                    )} />
                </Router>
                <Toast
                    isOpen={this.state.toast.open}
                    animation={"fall"}
                    animationOptions={{ duration: 0.2, delay: 0.4, timing: 'ease-in' }}
                >
                    <span>{this.state.toast.message}</span>
                </Toast>
            </div>
        );
    }
}

export default App;
