import React, { Component } from 'react';
import './App.css';
import {
    createFakeLobby,
    setPlayerReadyStatus,
    ListenToPlayersInLobby,
    getPlayersInLobby,
    createNewGame,
    addPlayerToLobby,
    stopListeningToLobby,
} from "./components/firebase-database.component";
import Lobby from './components/Lobby.js';
import MapView from './components/MapView.js';


class App extends Component {

  state = {
    playersInLobby: [],
    lobbyPropsUpdate: false,
  };

  componentDidMount() {
      ListenToPlayersInLobby((data) => {
          console.log('setting state :>> ');
          this.setState({
              playersInLobby: data,
              lobbyPropsUpdate: (!(this.state.lobbyPropsUpdate))
          });
          console.log('state :>> ', this.state);
      });
      createFakeLobby();
  }

  updatePlayerReadyState = (id, readyState) => {
    this.setState({
      allPlayers: (
        this.state.allPlayers.map((player) => {
          if (player.id === id) {
            player.ready = readyState;
          }
          return player;
        })
      )
    });
  };

  getPlayerById = (id) => {
    const allPlayers = this.state.playersInLobby;
    let player;
    for (let i = 0; i < allPlayers.length; i++) {
      player = allPlayers[i];
      if (player.playerId === id) {
        return player;
      }
    }
    return {
      id: id,
      name: '',
      ready: false,
      imgURL: ''
    }
  };


  render() {
    return (
        <div className="App">
          <MapView
          />
        </div>
    );
  }
}

export default App;
