import React, { Component } from 'react';
import './Lobby.css';
import Player from './Player.js';
import { Page as OnsPage } from 'react-onsenui';
import LobbyToolbar from './LobbyToolbar'


class Lobby extends Component {

    countDown = 30;

    state = {
        id: 0,
        ready: false,
        name: '',
        imgURL: '',
        allPlayers: [],
    };
    componentDidMount() {
        this.setState({
            id: this.props.user.playerId,
            imgURL: this.props.user.imgURL,
            name: this.props.user.playerName,
            ready: this.props.user.ready,
            allPlayers: this.props.allPlayers
        })
    }

    componentDidUpdate(prevProps) {

        if (prevProps.lobbyPropsUpdate !== this.props.lobbyPropsUpdate) {
            this.setState({
                id: this.props.user.playerId,
                imgURL: this.props.user.imgURL,
                name: this.props.user.playerName,
                ready: this.props.user.ready,
                allPlayers: this.props.allPlayers
            });

            let int = setInterval(() => {
                if (this.countDown <= 0) {
                    this.displayToast("game starting");
                    clearInterval(int);
                }

                if (this.props.allPlayers.length >= 2) {
                    this.displayToast(this.countDown, " seconds to game start");
                    this.countDown = this.countDown - 5;
                } else clearInterval(int);
            }, 5000);

            console.log('this.state.allPlayers :>> ', this.props.allPlayers);
        }
    }

    displayToast = (message) => {
        this.props.updateToastMessage(message);
        if (!(this.props.debounceToast)) {
            this.props.openToast();
        }
    };

    toggleReady = () => {
        this.countDown = 30;

        const readyState = !(this.state.ready);
        const id = this.state.id;
        this.setState({ ready: readyState });
        if (readyState) {
            this.props.addPlayerToLobbyFunc(this.props.user);
            this.displayToast("You joined the current game session");
        } else {
            this.props.removePlayerFromLobbyFunc(this.props.user);
            this.displayToast("You exited the current game session");
        }
    };

    render() {
        const allPlayers = this.state.allPlayers;
        return (
            <OnsPage
                className={'lobby-component'}
                renderToolbar={() => {
                    return (
                        <LobbyToolbar
                            title={'Lobby'}
                            user={this.props.user}
                            signOut={this.props.signOut}
                        />)
                }}
                style={{
                    backgroundColor: 'red',
                    backgroundSize: 0,

                }}
            >
                {/* <Player
                    className="player-item"
                    key={this.state.playerId}
                    style={{
                        marginTop: (this.state.ready) ? 80 : 0,
                        marginBottom: (this.state.ready) ? 20 : 60
                    }}
                    ready={this.state.ready}
                    player={this.props.user}
                /> */}

                <div
                    style={{
                        marginTop: 60,
                        marginBottom: 20
                    }}>

                </div>
                {allPlayers.map((player) => {

                    return (
                        <Player
                            className="player-item"
                            key={player.playerId}
                            id={player.playerId}
                            style={{
                                marginTop: 20,

                            }}
                            player={player} />)
                })}
                <div>
                    <button
                        className={`lobby-btn ${(this.state.ready) ? `lobby-exit-btn` : `lobby-join-btn`}`}
                        onClick={this.toggleReady}
                    />
                </div>
            </OnsPage>
        );
    }
}

export default Lobby;
