import React, {Component} from 'react';
import './App.css';
import { Provider } from 'react-redux';
import store from './redux/store.js';
import { createNewGame, createFakeLobby, addPlayerToLobby, stopListeningToLobby, ListenToPlayersInLobby, getPlayersInLobby, setPlayerReadyStatus } from "./components/firebase-database.component";
import Lobby from './components/Lobby.js';

class App extends Component{
  componentDidMount(){
    ListenToPlayersInLobby(function(data){
      console.log('data :', data);
    });



    createFakeLobby();
  }

  state = {
      id: 5,
      allPlayers: [],
  };

  updatePlayerReadyState = (id, readyState) =>{
      this.setState({
              allPlayers: (
                  this.state.allPlayers.map((player) => {
                      if(player.id === id){
                          player.ready = readyState;
                      }
                      return player;
                  })
              )
      });
  };

  getPlayerById = (id) => {
      const allPlayers = this.state.allPlayers;
      let player;
      for(let i = 0; i < allPlayers.length; i++){
          player = allPlayers[i];
          if(player.id === id){
              return player;
          }
      }
      return {
          id: id,
          name: '',
          ready: false,
          imgURL: ''
      }
  };

  render() {
      return (
        <Provider store={store}>
          <div className="App">
            <Lobby
              you={this.getPlayerById(this.state.id)}
              allPlayers={this.state.allPlayers}
              updatePlayerReadyState = {this.updatePlayerReadyState}
            />
          </div>
        </Provider>
      );
  }
}

export default App;
