import React, { Component } from 'react';
import './Lobby.css';
import Player from './Player.js';
import { Page as OnsPage } from 'react-onsenui';
import LobbyToolbar from './LobbyToolbar'
import { Redirect } from 'react-router-dom';




const COUNT_DOWN = 30;
class Lobby extends Component {
    countDown = COUNT_DOWN;
    countDownInterval;

    state = {
        redirect: false,
    }

    componentDidMount() {
    }

    startCountDown() {
        // console.log('start countdown');
        // this.displayToast(this.countDown + 5 + " seconds to start of the game");
        if (this.countDownInterval) { // restart counter if intervall is already active and gets started again
            this.countDown = COUNT_DOWN;
        }
        this.countDownInterval = setInterval(() => {
            if (this.countDown <= 0) {
                this.stopCountDown();
                this.displayToast("Game is starting!");
                this.setState({
                    redirect: true,
                })
                this.props.addPlayersToNewGame(this.props.allPlayers);
            } else {
                this.displayToast(this.countDown + " seconds to start of the game");
            }
            this.countDown = this.countDown - 5;
        }, 5000);
    }

    stopCountDown() {
        if (this.countDownInterval !== undefined && this.countDownInterval !== null) {
            // stop the countdown if too few players
            console.log('stop countdown');
            clearInterval(this.countDownInterval);
            this.countDown = COUNT_DOWN;
        }
    }

    componentDidUpdate(prevProps) {
        console.log('this.state.allPlayers :>> ', this.props.allPlayers);


        if (prevProps.allPlayers.length !== this.props.allPlayers.length) { // number of players in the lobby changed
            if (this.props.allPlayers.length >= 4 && this.props.userInLobby) {
                console.log('4 players or more');
                this.startCountDown();
            } else {
                this.stopCountDown();
            }
        }
    }

    displayToast = (message) => {
        this.props.updateToastMessage(message);
        if (!(this.props.debounceToast)) {
            this.props.openToast();
        }
    };

    toggleReady = () => {

        if (!this.props.userInLobby) {
            console.log('this.props.userInLobby :>> ', this.props.userInLobby);
            this.props.addPlayerToLobbyFunc(this.props.user);
            this.displayToast("You joined the current game session");
        } else {
            console.log('this.props.userInLobby :>> ', this.props.userInLobby);
            this.props.removePlayerFromLobbyFunc(this.props.user);
            this.displayToast("You exited the current game session");
        }
    };

    renderRedirect = () => {
        if (this.state.redirect) {
            return <Redirect push to={{
                pathname: '/map',
                state: {

                }
            }} />
        } else {
            return <React.Fragment />
        }
    };

    render() {

        const allPlayers = this.props.allPlayers;
        return (
            <React.Fragment>
                {this.renderRedirect()}
                <OnsPage
                    className={'lobby-component'}
                    renderToolbar={() => {
                        return (
                            <LobbyToolbar
                                title={'Lobby'}
                                user={this.props.user}
                                signOut={this.props.signOut}
                            />)
                    }}
                    style={{
                        backgroundColor: 'red',
                        backgroundSize: 0,

                    }}
                >
                    <div
                        style={{
                            marginTop: 60,
                            marginBottom: 20
                        }}>

                    </div>
                    {allPlayers.map((player) => {

                        return (
                            <Player
                                className="player-item"
                                key={player.playerId}
                                id={player.playerId}
                                style={{
                                    marginTop: 20,

                                }}
                                player={player} />)
                    })}
                    <div>
                        <button
                            className={`lobby-btn ${(this.props.userInLobby) ? `lobby-exit-btn` : `lobby-join-btn`}`}
                            onClick={this.toggleReady}
                        />
                    </div>
                </OnsPage>
            </React.Fragment>
        );
    }
}

export default Lobby;
