import React, {Component} from 'react';
import './Player.css';
import UserSVG from './svgs/User.js'



class Player extends Component{

    render() {
        const hasImgURL = (this.props.player.imgURL !== "" && this.props.player.imgURL !== undefined && this.props.player.imgURL !== null);
        return (
            <div className={`player-component ${(this.props.player.ready)? 'player-component-show' : 'player-component-hide' }`}>
                {(hasImgURL) ?
                    <img className={'player-img'} src={`${this.props.player.imgURL}`} alt=""/>:
                    <UserSVG className ={'player-img'}/>
                }
            </div>
        );
    }
}

export default Player;
