import React, {Component} from 'react';
import './Lobby.css';
import ScrollableDiv from './ScrollableDiv.js';
import Profile from './Profile.js';
import Player from './Player.js';



class Lobby extends Component{

    state = {
        id: 0,
        ready: false,
        name: '',
        imgURL: '',
        allPlayers: [],
    }
    componentDidMount(){ 
        this.setState({
            id: this.props.you.playerId,
            imgURL: this.props.you.imgURL,
            name: this.props.you.playerName,
            ready: this.props.you.ready,
            allPlayers: this.props.allPlayers
        })
    }

    componentDidUpdate(prevProps){
        if(prevProps.lobbyPropsUpdate !== this.props.lobbyPropsUpdate){
            this.setState({
                id: this.props.you.playerId,
                imgURL: this.props.you.imgURL,
                name: this.props.you.playerName,
                ready: this.props.you.ready,
                allPlayers: this.props.allPlayers
            })
        }
    }

    toggleReady = () => {
        const readyState = !(this.state.ready);
        const id = this.state.id;
        this.setState({ready: readyState});
        if (readyState) {
            this.props.addPlayerToLobby(this.props.user);
        } else {
            this.props.removePlayerFromLobby(this.props.user);
        }
        this.props.updatePlayerReadyState(id, readyState)
    };

    render() {
        console.log('this.props.you :>> ', this.props.you);
        const allPlayers = this.state.allPlayers;
        return (
            <div className={'lobby-component'}>
                <Profile imgURL = {this.state.imgURL}/>
                <ScrollableDiv
                    id='main-browseGames'
                    showScrollbar={true}
                    element = {

                        <React.Fragment>
                            <div className="player-item">
                                <Player ready={this.state.ready} player={this.props.you}/>
                            </div>
                            {allPlayers.map((player) => {

                                if(player.ready === true && player.playerId !== this.state.id){
                                    return (
                                        <div className="player-item" key={player.playerId}>
                                            <Player key={player.playerId} player={player}/>
                                        </div>)}
                                }

                            )}
                        </React.Fragment>
                    }
                />
                <button
                    className={`lobby-btn ${(this.state.ready) ? `lobby-exit-btn` : `lobby-join-btn`}`}
                    onClick={this.toggleReady}
                />
            </div>
        );
    }
}

export default Lobby;
