import React, {Component} from 'react';
import './Lobby.css';
import ScrollableDiv from './ScrollableDiv.js';
import Profile from './Profile.js';
import Player from './Player.js';



class Lobby extends Component{

    componentDidMount(){

    }

    render() {
        const allPlayers = this.props.allPlayers(),
            id = this.props.you.id,
            imgURL = this.props.you.imgURL,
            name = this.props.you.name,
            ready = this.props.you.ready;

        console.log({allPlayers});

        return (
            <div className={'lobby-component'}>
                <Profile imgURL = {imgURL}/>
                <ScrollableDiv
                    id='main-browseGames'
                    showScrollbar={true}
                    element = {

                        <React.Fragment>
                            <div className="player-item">
                                <Player ready={ready} player={this.props.you}/>
                            </div>
                            {allPlayers.then(players => players.map((player) => {
                                if(player.ready === true && player.id !== id){
                                    return (
                                        <div className="player-item" key={player.id}>
                                            <Player key={player.id} player={player}/>
                                        </div>)}
                                }

                            ))}
                        </React.Fragment>
                    }
                />
                <button
                    className={`lobby-btn ${(ready) ? `lobby-exit-btn` : `lobby-join-btn`}`}
                    onClick={this.props.updatePlayerReadyState(id, !ready)}
                />
            </div>
        );
    }
}

export default Lobby;
