import React, { Component } from 'react';
import './App.css';
import { Provider } from 'react-redux';
import store from './redux/store.js';
import { createNewGame, createFakeLobby, addPlayerToLobby, stopListeningToLobby, ListenToPlayersInLobby, getPlayersInLobby, setPlayerReadyStatus } from "./components/firebase-database.component";
import Lobby from './components/Lobby.js';

const promise1 = new Promise(function (resolve, reject) {
  ListenToPlayersInLobby((data) => {
    resolve(data);
    console.log('data :', data);
  });

});

class App extends Component {

  constructor(props) { 
    super(props); 
    this.state = { 
      playersInLobby: [], 
    }; 

    console.log(store.getState);

  }

  componentDidMount() {
    promise1.then(function (data) {
      this.setState({
        playersInLobby: data,
      })
    })
    createFakeLobby();
  }

  updatePlayerReadyState = (id, readyState) => {
    this.setState({
      allPlayers: (
        this.state.allPlayers.map((player) => {
          if (player.id === id) {
            player.ready = readyState;
          }
          return player;
        })
      )
    });
  };



  getPlayerById = (id) => {
    const allPlayers = getPlayersInLobby();
    let player;
    for (let i = 0; i < allPlayers.length; i++) {
      player = allPlayers[i];
      if (player.id === id) {
        return player;
      }
    }
    return {
      id: id,
      name: '',
      ready: false,
      imgURL: ''
    }
  };

  render() {
    return (
      <Provider store={store}>
        <div className="App">
         {/* 
          <Lobby
            you={this.getPlayerById('Bengt')}
            allPlayers={this.state.playersInLobby}
            updatePlayerReadyState={setPlayerReadyStatus}
         />
         */}
        </div>
      </Provider>
    );
  }
}

export default App;
