import React, {Component} from 'react';
import './Player.css';
import UserSVG from './svgs/User.js'



class Player extends Component{

    render() {
        const hasImgURL = (this.props.player.imgURL !== "" && this.props.player.imgURL !== undefined && this.props.player.imgURL !== null);
        return (
            <div
                className={`player-component ${this.props.className} ${(this.props.player.ready)? 'player-component-show' : 'player-component-hide' }`}
                key={this.props.key}
                style={{...this.props.style, color: 'white'}}
            >
                <div className={'content'}>
                    {(hasImgURL) ?
                        <img
                            className={'player-img'}
                            src={`${this.props.player.imgURL}`}
                            style={{
                                width: 100,
                                height: 100,
                            }}
                        />:
                        <UserSVG
                            className ={'player-img'}
                            style={{
                                width: 96,
                                height: 96,
                                border: '2px solid white'
                            }}
                        />
                    }
                    <div
                        style={{
                            marginTop: 5,
                        }}
                    >
                        {this.props.player.playerName}
                    </div>
                </div>


            </div>
        );
    }
}

export default Player;
