import React, {Component} from 'react';
import './ScrollableDiv.css';


class ScrollableDiv extends Component{

    state = {
    };

    componentDidMount(){
    }

    showScrollbarStyle = () => {
        if(this.props.showScrollbar){
            return 'show-scrollbar';
        }else{
            return 'hide-scrollbar';
        }
    };

    render() {

        return (
            <div className="scrollable-div">
                <div
                    className={`scrollable-div-container ${this.showScrollbarStyle()}`}
                    id={this.props.id}>
                    {this.props.element}
                </div>
            </div>
        );
    }

}



export default ScrollableDiv;
