import React, {Component} from 'react';
import 'onsenui/css/onsenui.css';
import 'onsenui/css/onsen-css-components.css';
import {Toolbar} from 'react-onsenui';
import './OnsToolbar.css';
import UserSVG from './svgs/User.js'


class LobbyToolbar extends Component {

    getProfileJSX = () => {
        const hasImgURL = (this.props.user.imgURL !== "" && this.props.user.imgURL !== undefined && this.props.user.imgURL !== null);
        const style = {
            width: 50,
            height: 50,
            color: 'white',
        };

        if(hasImgURL){
            return (
                <div
                    style={style}
                >
                    <img
                        src={this.props.user.imgURL}
                        className={'toolbar-profile-img'}
                    />
                </div>
            )
        }else{
            return (
                <div
                    style={style}
                >
                    <UserSVG
                        className={'toolbar-profile-img'}
                    />
                </div>

            )
        }

    };

    render() {
        return(
            <Toolbar
                style={{
                    backgroundColor: '#c00',
                    height: 104,
                    backgroundSize: 0,
                }}
            >
                <div className="left"
                     style={{
                         // borderWidth: 2,
                         // borderStyle: 'solid',
                         // borderColor: 'hotpink',
                         display: 'flex',
                         flexDirection: 'row',
                         flexWrap: 'wrap',
                         alignItems: 'center',
                         justifyContent: 'space-between',
                         color: 'white',
                     }}

                >
                    <section
                        className={'left-left'}
                    >
                        {this.getProfileJSX()}
                        <div>
                            {this.props.user.playerName}
                        </div>
                    </section>
                </div>

                <div className="center"
                     style={{
                         // borderWidth: 2,
                         // borderStyle: 'solid',
                         // borderColor: 'black',
                         display: 'flex',
                         flexDirection: 'row',
                         flexWrap: 'wrap',
                         alignItems: 'center',
                         justifyContent: 'center',
                         color: 'white',
                         fontSize: 40
                     }}
                >
                    {this.props.title}
                </div>

                <div className="right">
                    <button
                        className={'sign-out-btn'}
                        onClick={this.props.signOut}
                    >
                        Sign out
                    </button>
                </div>
            </Toolbar>
        )
    }
}

export default LobbyToolbar;
